import '../../styles/Gallery/Thumb.css'
function Thumb({ id, title, cover, pictures, description, host, rating, location, equipments, tags }) {
	return (
		<li key={id} className='kasa-housing-item'>
			<img className='kasa-housing-item-cover' src={cover} alt={`${title} cover`} />
			<div>
				<h2>{title}</h2>
			</div>
		</li>
	)
}

export default Thumb
