import '../../styles/Gallery/Thumb.css'
import {Link} from 'react-router-dom'

function Thumb({ id, title, cover}) {
	return (
		<Link>
			<img src={cover} alt={title}/>
			<h2>{title}</h2>
		</Link>

	)
}

export default Thumb
